import React, {useState, useEffect} from 'react';
import './App.css';

const App = () => {

  const [data, setData] = useState([])
  const [currentpage, setCurrentpage] = useState(5)
  const imagePart = "https://image.tmdb.org/t/p/w185"

  const fetching = () => {
    fetch(`https://api.themoviedb.org/3/person/popular?api_key=df8b08ecb436696fee41a00f8d87a540&language=en&page=${currentpage}`)
    .then(res => res.json())
    .then(data => setData(data.results))
  }

  useEffect(() => {
    fetching()
  }, [])
  console.log(data, 'if data is fetched')

  return (
  <div className='App'>
      <div className='actors'>
        {
          data.map(item => {
            const imageUrl =imagePart +item.profile_path
            return (
            <div className='actor'>
              <p>{item.name}</p>
              <img src={imageUrl} alt=''></img>
              <div className="name"></div>
              <div className="details"></div>
            </div>
            )
          })
        }
      </div>
  </div>
  )
}

export default App;
