import React, {useState, useEffect} from 'react';
import './App.css';

const App = () => {

  const [data, setData] = useState([])
  const [currentpage, setCurrentpage] = useState(10)
  const [isloading, setLoading] = useState(false)
  const imagePart = "https://image.tmdb.org/t/p/w185"

  const fetching = () => {
    setTimeout(()=>{

      fetch(`https://api.themoviedb.org/3/person/popular?api_key=df8b08ecb436696fee41a00f8d87a540&language=en&page=${currentpage}`)
      .then(res => res.json())
      .then(data => {
        setData(data.results);
        setLoading(true);
      })  
    }, 2000)
  }

  useEffect(() => {
    fetching()
  }, [])
  console.log(data, 'if data is fetched')

  return (
  <div className='App'>
      <div className='actors'>
        {
         isloading ?  data.map(item => {
            const imageUrl =imagePart +item.profile_path
            //const knownFor = item.known_for.map(el=> el.title)

            return (
            <div className='actor'>
              <img src={imageUrl} alt=''></img>
              <div className="name"><h4>{item.name}</h4></div>
              {/* <div className="details">
                <h5>Movies:</h5>
                <h5>{knownFor}</h5>
              </div> */}
              <button>More details</button>
            </div>
            )
          })
          : 
          <div className="loader">
            <img className="loader-image" src="https://i.pinimg.com/originals/86/c0/25/86c025297e8b557da4522d2b48cd1757.gif" alt=""></img>
          </div>
        }
      </div>
  </div>
  )
}

export default App;
